import React, { useState } from "react";
import { View, Text, TextInput, Button, FlatList, StyleSheet } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from "@react-navigation/stack";

const TipsPage = () => {
  const [tipText, setTipText] = useState("");
  const [tips, setTips] = useState([]);

  const handleAddTip = () => {
    if (tipText) {
      const user = { username: "User123" };
      const newTip = { text: tipText, user: user };

      setTips([newTip, ...tips]);
      setTipText("");
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.listTitle}>Tips:</Text>
      <FlatList
        data={tips}
        keyExtractor={(item, index) => index.toString()}
        renderItem={({ item }) => (
          <View style={styles.commentContainer}>
            <Text style={styles.tipAndUser}>
              <Text style={styles.username}>{item.user.username}:</Text>{" "}
              {item.text}
            </Text>
          </View>
        )}
        ListEmptyComponent={() => <Text style={styles.emptyList}>No tips yet</Text>}
      />
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Enter your tip here"
          value={tipText}
          onChangeText={(text) => setTipText(text)}
        />
        <Button title="Add Tip" onPress={handleAddTip} />
      </View>
    </View>
  );
};

const MenuBar = () => {
  return (
    <Text style={{ fontSize: 20, fontWeight: "bold", padding: 16 }}>
      Menu Bar
    </Text>
  );
};

const Stack = createStackNavigator();

const TipsStackScreen = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="Tips"
        component={TipsPage}
        options={{
          headerTitle: (props) => <MenuBar {...props} />,
        }}
      />
    </Stack.Navigator>
  );
};

export default function App() {
  return (
    <NavigationContainer>
      <TipsStackScreen />
    </NavigationContainer>
  );
}
