import React, { useState } from "react";
import { View, Text, TextInput, Button, FlatList, StyleSheet } from "react-native";
import Heading from "../components/landing-screen/Heading"; // Import the Heading component

const TipsPage = () => {
  const [tipText, setTipText] = useState("");
  const [tips, setTips] = useState([]);
  const [isListVisible, setListVisible] = useState(false); // State to manage list visibility

  const handleAddTip = () => {
    if (tipText) {
      // Assuming you have a user object with a 'username' property
      const user = { username: "User123" }; // Replace with the actual user object
      // Create a new tip object with both tipText and the user who posted it
      const newTip = { text: tipText, user: user };

      // Add the newTip to the tips array
      setTips([newTip, ...tips]);

      // Clear the input
      setTipText("");
    }
  };

  // Function to toggle the list visibility
  const toggleListVisibility = () => {
    setListVisible(!isListVisible);
  };

  return (
    <View style={styles.container}>
      {/* Clickable Heading to toggle list visibility */}
      <View style={styles.headerRoot} onTouchEnd={toggleListVisibility}>
        <Heading />
      </View>

      {/* Render the list conditionally */}
      {isListVisible && (
        <FlatList
          data={tips}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({ item }) => (
            <View style={styles.commentContainer}>
              <Text style={styles.tipAndUser}>
                <Text style={styles.username}>{item.user.username}:</Text>{" "}
                {item.text}
              </Text>
            </View>
          )}
          ListEmptyComponent={() => <Text style={styles.emptyList}>No tips yet</Text>}
        />
      )}

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Enter your tip here"
          value={tipText}
          onChangeText={(text) => setTipText(text)}
        />
        <Button title="Add Tip" onPress={handleAddTip} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: "#fff",
  },
  listTitle: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 8,
  },
  commentContainer: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 4,
    padding: 8,
    marginBottom: 8,
  },
  tipAndUser: {
    flexDirection: "row",
    alignItems: "center",
  },
  username: {
    fontWeight: "bold",
    marginRight: 8,
  },
  tip: {
    fontSize: 16,
  },
  emptyList: {
    fontSize: 16,
    color: "#777",
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginTop: 16,
  },
  input: {
    flex: 1,
    marginRight: 8,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 4,
    padding: 8,
  },
  headerRoot: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginHorizontal: 35,
    cursor: "pointer", // Add pointer cursor to indicate clickability
  },
});

export default TipsPage;
