import React, { useState, useEffect } from 'react';
import Heading from '../components/landing-screen/Heading';
import axios from 'axios';

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    location: '',
    incidentType: '',
    otherIncidentType: '',
    description: '',
  });
  const [locations, setLocations] = useState([]); // State for location data

  useEffect(() => {
    // Fetch locations from the server when the component mounts
    axios
      .get('/api/locations') // Use the same endpoint as defined in your server
      .then((response) => {
        setLocations(response.data);
      })
      .catch((error) => {
        console.error('Error fetching locations:', error);
      });
  }, []); // The empty dependency array ensures this effect runs only once

  const handleChange = (e) => {
    const { name, value } = e.target;
    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!incident.incidentType) {
      alert('Please select an incident type');
      return;
    }
    if (incident.incidentType === 'Other' && !incident.otherIncidentType) {
      alert('Please enter another incident type');
      return;
    }
    try {
      const formData = {
        location: incident.location,
        incidentType: incident.incidentType,
        otherIncidentType: incident.otherIncidentType,
        description: incident.description,
      };
      const apiUrl = 'http://localhost:YOUR_PORT/api/submit'; // Replace with your server URL
      const response = await axios.post(apiUrl, formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (response.status === 200) {
        console.log('Response data:', response.data);
        setIncident({
          location: '',
          incidentType: '',
          otherIncidentType: '',
          description: '',
        });
      } else {
        // Handle other status codes if needed
      }
    } catch (error) {
      console.error('Axios error:', error);
    }
  };

  return (
    <div className="container" style={{ backgroundColor: 'white' }}>
      <style>
        {`
        .container {
          max-width: 400px;
          margin: 0 auto;
          padding: 20px;
          font-family: 'Poppins', sans-serif;
        }
        /* Rest of your styling */
        `}
      </style>
      <Heading />
      <div className="container">
        <h2>Incident Reporting</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Enter Location:</label>
            <select
              name="location"
              value={incident.location}
              onChange={handleChange}
            >
              <option value="">Select Location</option>
              {locations.map((location) => (
                <option key={location.id} value={location.name}>
                  {location.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Incident Type:</label>
            <div className="checkbox-container">
              {/* Rest of your incident type code */}
            </div>
          </div>
          <div className="form-group">
            <label className="incident-description-label">Incident Description:</label>
            <textarea
              name="description"
              value={incident.description}
              onChange={handleChange}
              className="incident-description"
            />
          </div>
          <div className="btn-container">
            <button type="submit" className="btn">
              Submit
            </button>
          </div>
        </form>
        {!incident.incidentType && (
          <div style={{ color: 'red', textAlign: 'center' }}>
            Please select an incident type.
          </div>
        )}
      </div>
    </div>
  );
};

export default IncidentReportForm;
