import React, { useState } from 'react';
import { View, TextInput, Button, Text, StyleSheet } from 'react-native';

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    date: '',
    location: '',
    incidentType: '',
    description: '',
  });

  const handleChange = (name, value) => {
    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    // Handle form submission (e.g., send data to a server, save to a database)
    console.log('Incident data:', incident);
    // Clear the form fields after submission
    setIncident({
      date: '',
      location: '',
      incidentType: '',
      description: '',
    });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Incident Reporting Form</Text>
      <View style={styles.form}>
        <TextInput
          style={styles.input}
          placeholder="Date"
          value={incident.date}
          onChangeText={(text) => handleChange('date', text)}
        />
        <TextInput
          style={styles.input}
          placeholder="Location"
          value={incident.location}
          onChangeText={(text) => handleChange('location', text)}
        />
        <TextInput
          style={styles.input}
          placeholder="Incident Type"
          value={incident.incidentType}
          onChangeText={(text) => handleChange('incidentType', text)}
        />
        <TextInput
          style={[styles.input, styles.textarea]}
          placeholder="Incident Description"
          value={incident.description}
          onChangeText={(text) => handleChange('description', text)}
          multiline={true}
        />
        <Button title="Submit" onPress={handleSubmit} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'lightgray',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 20,
    marginBottom: 20,
  },
  form: {
    width: '80%',
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
    marginBottom: 10,
    padding: 10,
  },
  textarea: {
    height: 100,
  },
});

export default IncidentReportForm;
