import React, { useState } from "react";
import { View, Text, TextInput, Button, FlatList, StyleSheet } from "react-native";
import Heading from "../components/landing-screen/Heading";
const TipsPage = () => {
  const [tipText, setTipText] = useState("");
  const [tips, setTips] = useState([]);

  const handleAddTip = () => {
    if (tipText) {
      // Assuming you have a user object with a 'username' property
      const user = { username: "User123" }; // Replace with the actual user object
  const navigation = useNavigation(); // Initialize the navigation object
      // Create a new tip object with both tipText and the user who posted it
      const newTip = { text: tipText, user: user };

      // Add the newTip to the tips array
      setTips([newTip, ...tips]);

      // Clear the input
      setTipText("");
    }
  };

  return (
    <Heading></Heading>
      
    
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: "#fff",
  },
  listTitle: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 8,
  },
  commentContainer: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 4,
    padding: 8,
    marginBottom: 8,
  },
  tipAndUser: {
    flexDirection: "row",
    alignItems: "center",
  },
  username: {
    fontWeight: "bold",
    marginRight: 8,
  },
  tip: {
    fontSize: 16,
  },
  emptyList: {
    fontSize: 16,
    color: "#777",
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginTop: 16,
  },
  input: {
    flex: 1,
    marginRight: 8,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 4,
    padding: 8,
  },
});

export default TipsPage;
