import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
} from 'react-native';

const Dropdown = () => {
  const [isDropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  return (
    <View style={styles.container}>
      <View style={styles.dropdown}>
        <TouchableOpacity onPress={toggleDropdown} style={styles.dropbtn}>
          <Text style={{ color: 'white', fontSize: 16 }}>Dropdown</Text>
        </TouchableOpacity>
        {isDropdownVisible && (
          <View style={styles.dropdownContent}>
            <TouchableOpacity onPress={toggleDropdown}>
              <Text>Home</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={toggleDropdown}>
              <Text>About</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={toggleDropdown}>
              <Text>Contact</Text>
            </TouchableOpacity>
          </View>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
  dropdown: {
    position: 'relative',
    display: 'inline-block',
  },
  dropbtn: {
    backgroundColor: '#04AA6D',
    padding: 16,
    fontSize: 16,
    border: 'none',
    cursor: 'pointer',
  },
  dropdownContent: {
    position: 'absolute',
    backgroundColor: '#f1f1f1',
    minWidth: 160,
    overflow: 'auto',
    shadowColor: 'rgba(0, 0, 0, 0.2)',
    shadowOffset: { width: 0, height: 8 },
    shadowRadius: 16,
    zIndex: 1,
    right: 0,
  },
});

export default Head;
