import React, { useState } from 'react';
import Heading from "../components/landing-screen/Heading";

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    location: '',
    incidentType: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Incident data:', incident);
    setIncident({
      location: '',
      incidentType: '',
      description: '',
    });
  };

  return (
    <div className="container">
      <style>
        {`
        .container {
          max-width: 400px;
          margin: 0 auto;
          padding: 20px;
          font-family: 'Poppins', sans-serif;
        }

        h2 {
          text-align: center;
        }

        label {
          font-weight: bold;
          display: block;
          margin-bottom: 5px;
        }

        input, textarea {
          width: 100%;
          padding: 8px;
          border: 1px solid #ccc;
          border-radius: 6px;
          font-family: 'Poppins', sans-serif;
          margin-bottom: 10px;
        }

        .radio-container {
          display: flex;
          flex-direction: column;
        }

        .radio-label {
          display: flex;
          align-items: center; /* Center radio button and text vertically */
          margin-bottom: 5px;
        }

        .radio-input {
          margin-right: 10px; /* Add margin to create space between radio and text */
        }

        .btn-container {
          display: flex;
          justify-content: flex-end;
        }

        .btn {
          width: 92px;
          height: 24px;
          background-color: black;
          color: white;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          font-family: 'Poppins', sans-serif;
        }

        .btn:hover {
          background-color: #000000;
        }

        .enter-location {
          height: 29px;
        }

        .incident-description {
          height: 127px;
        }

        .incident-description-label {
          text-align: center;
        }
        `}
      </style>
      <Heading />
      <div className="container">
        <h2>Incident Reporting</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Enter Location:</label>
            <input
              type="text"
              name="location"
              value={incident.location}
              onChange={handleChange}
              className="enter-location"
            />
          </div>

          <div className="form-group">
            <label>Incident Type:</label>
            <div className="radio-container">
              <label className="radio-label">
                <input
                  type="radio"
                  name="incidentType"
                  value="Type 1"
                  onChange={handleChange}
                  checked={incident.incidentType === 'Type 1'}
                  className="radio-input"
                />
                Car jacking
              </label>
              <label className="radio-label">
                <input
                  type="radio"
                  name="incidentType"
                  value="Type 2"
                  onChange={handleChange}
                  checked={incident.incidentType === 'Type 2'}
                  className="radio-input"
                />
                Robbery
              </label>
              <label className="radio-label">
                <input
                  type="radio"
                  name="incidentType"
                  value="Type 3"
                  onChange={handleChange}
                  checked={incident.incidentType === 'Type 3'}
                  className="radio-input"
                />
                Kidnapping
              </label>
              <label className="radio-label">
                <input
                  type="radio"
                  name="incidentType"
                  value="Type 4"
                  onChange={handleChange}
                  checked={incident.incidentType === 'Type 4'}
                  className="radio-input"
                />
                
              </label>
            </div>
          </div>

          <div className="form-group">
            <label className="incident-description-label">Incident Description:</label>
            <textarea
              name="description"
              value={incident.description}
              onChange={handleChange}
              className="incident-description"
            />
          </div>

          <div className="btn-container">
            <button type="submit" className="btn">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default IncidentReportForm;
