import React, { useState } from "react";
import { Colors } from "../assets/Colors";
import { Text, SafeAreaView, StyleSheet, View } from "react-native";
import LocationSvg from "../assets/svg/LocationSvg";
import InputComponent from "../components/global/InputComponent";
import RoundedButton from "../components/global/RoundedButton";
import axios from 'axios';

const RegisterScreen = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [error, setError] = useState('');

  const registerFun = async () => {
    try {
      const response = await axios.post('http://192.168.27.9:3002/register', userData);
      
      // Check if the response status is OK (200)
      if (response.status === 200) {
        // Registration was successful
        console.log('Registration successful:', response.data);
        // You can navigate to the login screen or perform other actions here
      } else {
        // Handle unexpected status codes
        setError(`Registration failed with status code ${response.status}`);
      }
    } catch (error) {
      if (error.message === 'Network Error') {
        setError('Network error. Please check your connection and try again.');
      } else {
        setError('An error occurred. Please try again later.');
      }
      console.error('Error:', error);
    }
  };

  const handleInputChange = (key, value) => {
    // Update the userData state when the input fields change
    setUserData({
      ...userData,
      [key]: value,
    });
  };

  return (
    <SafeAreaView style={styles.root}>
      <View style={styles.svgtext}>
        <LocationSvg />
        <Text style={styles.headerText}>Register your account</Text>
      </View>
      <View style={styles.fields}>
        <InputComponent
          placeholder="Username"
          mode="text"
          onChangeText={(text) => handleInputChange("username", text)}
        />
        <InputComponent
          placeholder="Email"
          mode="email"
          onChangeText={(text) => handleInputChange("email", text)}
        />
        <InputComponent
          placeholder="Password"
          mode="text"
          secure={true}
          onChangeText={(text) => handleInputChange("password", text)}
        />
        <InputComponent
          placeholder="Confirm Password"
          mode="text"
          secure={true}
          onChangeText={(text) => handleInputChange("confirmPassword", text)}
        />
        <RoundedButton
          title="Register"
          btnColor={Colors.black}
          onPressedFun={registerFun}
        />
        {error && <Text style={styles.errorText}>{error}</Text>}
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  root: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: Colors.gray,
  },
  svgtext: {
    gap: 31,
    alignItems: "center",
    marginBottom: 30,
  },
  headerText: {
    fontSize: 20,
    fontWeight: "700",
  },
  fields: {
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    gap: 20,
  },
  errorText: {
    color: "red",
    marginTop: 10,
  },
});

export default RegisterScreen;
