import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import axios from 'axios';

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    incidentType: '',
    description: '',
    location: '',
  });

  const [error, setError] = useState('');

  const handleChange = (name, value) => {
    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = async () => {
    try {
      // Send a POST request to your server's /reports endpoint
      const response = await axios.post('http://localhost:3003/reports', {
        incident_type: incident.incidentType,
        rep_description: incident.description,
        name: incident.location, // Use 'name' as the location field for the server
        citizen_id: 1, // You might want to specify the citizen_id
      });

      // Handle the server response here
      console.log('Server response:', response.data);

      // Clear the form fields after submission
      setIncident({
        incidentType: '',
        description: '',
        location: '',
      });
    } catch (error) {
      // Handle any errors that occurred during the request
      console.error('Error:', error);
      setError('An error occurred while submitting the form.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Incident Reporting</Text>
      <View style={styles.formGroup}>
        <Text>Incident Type:</Text>
        <TextInput
          style={styles.input}
          value={incident.incidentType}
          onChangeText={(text) => handleChange('incidentType', text)}
        />
      </View>

      <View style={styles.formGroup}>
        <Text>Incident Description:</Text>
        <TextInput
          style={styles.input}
          value={incident.description}
          onChangeText={(text) => handleChange('description', text)}
        />
      </View>

      <View style={styles.formGroup}>
        <Text>Location:</Text>
        <TextInput
          style={styles.input}
          value={incident.location}
          onChangeText={(text) => handleChange('location', text)}
        />
      </View>

      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={styles.buttonText}>Submit</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  heading: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  formGroup: {
    marginBottom: 15,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 6,
    padding: 8,
  },
  button: {
    backgroundColor: 'black',
    borderRadius: 6,
    padding: 10,
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
  },
});

export default IncidentReportForm;
