import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Modal, StyleSheet } from 'react-native';

const Heading2 = () => {
  const [isDropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={toggleDropdown} style={styles.dropbtn}>
        <Text>Dropdown</Text>
      </TouchableOpacity>

      <Modal
        transparent={true}
        visible={isDropdownVisible}
        onRequestClose={toggleDropdown}
      >
        <View style={styles.modalContent}>
          <TouchableOpacity onPress={toggleDropdown}>
            <View style={styles.dropdownContent}>
              <TouchableOpacity onPress={() => alert('Link 1')}>
                <Text>Link 1</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => alert('Link 2')}>
                <Text>Link 2</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => alert('Link 3')}>
                <Text>Link 3</Text>
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  dropbtn: {
    padding: 10,
    borderWidth: 1,
    borderColor: '#000',
  },
  modalContent: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  dropdownContent: {
    width: 150,
    backgroundColor: '#fff',
    borderWidth: 1,
    borderColor: '#000',
    padding: 10,
  },
});

export default Heading2;
