import React, { useState } from 'react';
import Heading from "../components/landing-screen/Heading";
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3003';
const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    location: '',
    incidentType: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post(`${API_BASE_URL}/reports`, {
        name: incident.location,
        incident_type: incident.incidentType,
        rep_description: incident.description,
        citizen_id: 1, // Replace with the actual citizen ID
      });

      if (response.status === 201) {
        console.log('Report created successfully');
        setIncident({
          location: '',
          incidentType: '',
          description: '',
        });
      }
    } catch (error) {
      console.error('Error creating report:', error);
    }
  };

  return (
    <div className="container">
      <style>
        {/* Your CSS styles here */}
      </style>
      <Heading />
      <div className="container">
        <h2>Incident Reporting</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Enter Location:</label>
            <input
              type="text"
              name="location"
              value={incident.location}
              onChange={handleChange}
              className="enter-location"
            />
          </div>

          <div className="form-group">
            <label>Incident Type:</label>
            <div className="radio-container">
              <label className="radio-label">
                <input
                  type="radio"
                  name="incidentType"
                  value="Type 1"
                  onChange={handleChange}
                  checked={incident.incidentType === 'Type 1'}
                  className="radio-input"
                />
                Car jacking
              </label>
              {/* Add similar radio buttons for other incident types */}
            </div>
          </div>

          <div className="form-group">
            <label className="incident-description-label">Incident Description:</label>
            <textarea
              name="description"
              value={incident.description}
              onChange={handleChange}
              className="incident-description"
            />
          </div>

          <div className="btn-container">
            <button type="submit" className="btn">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default IncidentReportForm;
