// ProfileScreen.js
import React, { useState } from 'react';
import { View, Text, Image, TextInput, Button, StyleSheet, FlatList } from 'react-native';

const ProfileScreen = () => {
  const [name, setName] = useState('John Doe');
  const [username, setUsername] = useState('johndoe');
  const [email, setEmail] = useState('johndoe@example.com');
  const [isEditing, setIsEditing] = useState(false);

  const saveChanges = () => {
    setIsEditing(false);
    // You can implement code here to save the edited details to your backend or storage.
  };

  const data = [
    { label: 'Name', value: name },
    { label: 'Username', value: username },
    { label: 'Email', value: email },
  ];

  return (
    <View style={styles.container}>
      <Image
        //source={require('./profile.jpg')} // Your profile picture
        style={styles.profileImage}
      />
      <FlatList
        data={data}
        keyExtractor={(item) => item.label}
        renderItem={({ item }) => (
          <View style={styles.infoRow}>
            <Text style={styles.label}>{item.label}:</Text>
            {isEditing ? (
              <TextInput
                style={styles.input}
                value={item.value}
                onChangeText={(text) => {
                  if (item.label === 'Name') setName(text);
                  if (item.label === 'Username') setUsername(text);
                  if (item.label === 'Email') setEmail(text);
                }}
              />
            ) : (
              <Text style={styles.value}>{item.value}</Text>
            )}
          </View>
        )}
      />
      {isEditing ? (
        <Button title="Save Changes" onPress={saveChanges} color="black" />
      ) : (
        <Button title="Edit Profile" onPress={() => setIsEditing(true)} color="white" />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'lightgrey', // Set the background color to grey
  },
  profileImage: {
    width: 150,
    height: 150,
    borderRadius: 75,
    marginBottom: 20,
  },
  infoRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1, // Add a border
    borderColor: 'lightgrey', // Set border color to grey
    padding: 10,
    borderRadius: 5,
  },
  label: {
    width: 100,
    fontSize: 16,
    fontWeight: 'bold',
  },
  value: {
    flex: 1,
    fontSize: 16,
    color: 'gray',
  },
  input: {
    flex: 1,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
    paddingLeft: 10,
  },
});

export default ProfileScreen;
