import React, { useState } from 'react';
import Heading from "../components/landing-screen/Heading";

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    date: '',
    location: '',
    incidentType: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to a server, save to a database)
    console.log('Incident data:', incident);
    // Clear the form fields after submission
    setIncident({
      date: '',
      location: '',
      incidentType: '',
      description: '',
    });
  };

  return (
    <div className="container">
      <style>
        {`
        .container {
          max-width: 400px;
          margin: 0 auto;
          padding: 20px;
          font-family: 'Poppins', sans-serif;
        }

        h2 {
          text-align: center;
        }

        label {
          display: block;
          font-weight: bold;
        }

        input, select, textarea {
          width: 100%;
          padding: 8px;
          border: 1px solid #ccc;
          border-radius: 6px;
          font-family: 'Poppins', sans-serif;
        }

        select {
          height: 36px;
        }

        textarea {
          height: 100px;
          margin-bottom: 10px; /* Add margin to create space under textareas */
        }

        .btn-container {
          display: flex;
          justify-content: flex-end; /* Move button to the right */
          align-items: center; /* Center vertically with textarea */
        }

        .btn {
          width: 92px;
          height: 24px;
          background-color: black;
          color: white;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          font-family: 'Poppins', sans-serif;
        }

        .btn:hover {
          background-color: #000000; /* Darken on hover */
        }

        /* Add styles for Enter Location text area */
        .enter-location {
          width: 310px;
          height: 29px;
        }

        /* Add styles for Date input */
        .date-input {
          width: 310px;
          height: 20px;
        }
        `}
      </style>
      <Heading />
      <div className="container">
        <h2>Incident Reporting</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Enter Location:</label>
            <input
              type="text"
              name="location"
              value={incident.location}
              onChange={handleChange}
              className="enter-location"
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              name="date"
              value={incident.date}
              onChange={handleChange}
              className="date-input"
            />
          </div>

          <div className="form-group">
            <label>Incident Type:</label>
            <select
              name="incidentType"
              value={incident.incidentType}
              onChange={handleChange}
            >
              <option value="">Select Type</option>
              <option value="Type 1">Car jacking</option>
              <option value="Type 2">Robbery</option>
              <option value="Type 3">Kidnapping</option>
              <option value="Type 4">Other</option>
            </select>
          </div>
          <div className="form-group">
            <label>Incident Description:</label>
            <textarea
              name="description"
              value={incident.description}
              onChange={handleChange}
            />
          </div>
          <div className="btn-container">
            <button type="submit" className="btn">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default IncidentReportForm;
