import React, { useEffect, useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { BarChart } from 'react-native-chart-kit';
import { Dimensions } from 'react-native';
import axios from 'axios';
import HomeScreen from './HomeScreen';
import Heading from '../components/landing-screen/Heading';
import { BASE_URL } from '../assets/constants/ConstantData';
import AsyncStorage from '@react-native-async-storage/async-storage';

const screenWidth = Dimensions.get('window').width;

export default function Graph() {
  const [incidentType, setIncidentType] = useState([]);
  const [numPerCrime, setNumPerCrime] = useState([]);
  const [findLocation, setFindLocation] = useState('');
  const [isFound, setIsFound] = useState(false);
  const [citizenId, setCitizenId] = useState('');
  const [citizenName, setCitizenName] = useState('');

  const api = `${BASE_URL}:3008/get_survey_summary_report/`;
  const welcomeMessage = `Hi ${citizenName}`;

  const getItemStorage = async () => {

    const keys = ['citizen_id', 'citizen_name'];
    AsyncStorage.multiGet(keys)
      .then((keyValuePairs) => {
        keyValuePairs.forEach(([key, value]) => {
          if(key === 'citizen_id') setCitizenId(value)
          if (key === 'citizen_name') setCitizenName(value)
          console.log(`Key: ${key}, Value: ${value}`);
        });
      })
      .catch((error) => {
        console.error('Error retrieving data: ', error);
    });
  }

  useEffect(() => {
    getItemStorage()

    axios.get(api + 'mabopaneTT').then(
      (res) => {
        console.log(res.data);
        // Remove duplicates from incident types using Set
        const uniqueIncidentTypes = [...new Set(res.data.incident_types)];
        setIncidentType(uniqueIncidentTypes);
        setNumPerCrime(res.data.numper_per_incedences);
        setIsFound(res.data.success);
      },
      (err) => {
        console.log(err);
      }
    );
  }, []);

  const chartConfig = {
    backgroundGradientFrom: '#f0f0f0',
    barPercentage: 7.8,
    backgroundGradientFromOpacity: 0.8,
    backgroundGradientTo: 'white',
    backgroundGradientToOpacity: 0.8,
    color: (opacity = 1) => {
    const colors = ['#007BFF', '#34C759', '#FFD60A', '#FF453A', '#7950F2']; // Define your custom colors
    const colorIndex = Math.floor(colors.length * Math.random()); // Randomly select a color from the array
    return `${colors[colorIndex]}${Math.floor(opacity * 255).toString(16)}`; // Apply the color with opacity
    },
    strokeWidth: 2,
    barPercentage: 0.7,
    useShadowColorFromDataset: false,
    fillShadowGradient: 'rgba(0, 123, 255, 0.4)',
    fillShadowGradientOpacity: 0.4,
    decimalPlaces: 0,
    propsForLabels: {
      fontSize: 14,
      fontWeight: 'bold',
    },
    propsForDots: {
      r: '6',
      strokeWidth: '2',
      stroke: '#007BFF',
    },
  };

  function post() {
    console.log(findLocation);
    axios.get(api + findLocation).then(
      (res) => {
        console.log(res.data);
        setIncidentType(res.data.incident_types);
        setNumPerCrime(res.data.numper_per_incedences);
        setIsFound(res.data.success);
      },
      (err) => {
        console.log(err);
      }
    );
  }

  const data = {
  labels: incidentType,
  datasets: [
    {
      data: numPerCrime,
      color: (opacity = 1) => {
        // Define individual colors for each bar
        const colors = ['#007BFF', '#34C759', '#FFD60A', '#FF453A', '#7950F2'];
       return `${colors[numPerCrime.indexOf(opacity)]}${Math.floor(opacity * 255).toString(16)}`;
      },
    },
  ],
};


  let displayGraph = (
    <View style={styles.graphContainer}>
      <BarChart
        data={data}
        width={screenWidth}
        height={260}
        chartConfig={chartConfig}
        verticalLabelRotation={1}
        showBarTops
        showValuesOnTopOfBars
        yAxisSuffix=""
        withInnerLines
        fromZero
        animation
      />
    </View>
  );

  return (
    <View style={styles.container}>

      <View style={styles.btn}>
        <View style={styles.massage}>
        <Text style={styles.welcomeMessage}>{welcomeMessage}!</Text>
        </View>

      <Text style={styles.locationText}>
        {/* You are viewing results for {findLocation} */}
      </Text>
      <TextInput
        style={[styles.input,{backgroundColor:'white'}]}
        placeholder="Find HotSpot Location"
        onChangeText={(e) => setFindLocation(e)}
      />
      <TouchableOpacity style={styles.button} onPress={() => post()}>
        <Text style={styles.buttonText}>Find Location</Text>
      </TouchableOpacity>
      </View>

      <View>
        {isFound === true && <View>{displayGraph}</View>}
        {isFound === false && (
          <Text style={styles.errorMessage}>
            THE LOCATION ENTERED IS NOT CONSIDERED A DANGER ZONE! NO CRIMES REPORTED
          </Text>
        )}
      </View>
      <HomeScreen  />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E3E9EC',
    flexDirection: 'column',
    alignItems: 'relative',


  },

  btn:{
    alignItems:'center'
  },

  welcomeMessage:{
    marginTop:20,
    margin:20,
    alignSelf: 'flex-start',
    fontWeight: 'bold',
  },
  locationText: {
    marginLeft: 10,
    marginBottom: 10,
  },
  graphContainer: {
    marginVertical: 20,
    borderRadius: 10,
    backgroundColor: 'white',
    padding: 10,
    elevation: 5,
  },
  input: {
    margin:20,
    width: '80%',
    height: 40,
    Color:'#fffff',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  button: {
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 5,
    width:120,
    margin:20,
    alignItems:'center',
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
  },
  errorMessage: {
    marginTop:10,
    color: 'red',
    margin:40,
    alignSelf: 'center',
  },
});
