import React, { useState } from "react";
import { Colors } from "../assets/Colors";
import { Text, SafeAreaView, StyleSheet, View } from "react-native";
import LocationSvg from "../assets/svg/LocationSvg";
import InputComponent from "../components/global/InputComponent";
import RoundedButton from "../components/global/RoundedButton";
import axios from 'axios';

const RegisterScreen = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const registerFun = async () => {
    try {
      // Create a user object with the registration data
      const user = {
        username,
        email,
        password,
        confirmPassword,
      };

      // Make a POST request to your server's registration endpoint
      const response = await axios.post('https://your-api-url.com/register', user);

      // Handle the server response here
      console.log('Server response:', response.data);

      // You can also navigate to the login screen or perform other actions based on the response
    } catch (error) {
      // Handle any errors that occurred during the request
      console.error('Error:', error);
    }
  };

  return (
    <SafeAreaView style={styles.root}>
      <View style={styles.svgtext}>
        <LocationSvg />
        <Text style={styles.headerText}>Register your account</Text>
      </View>
      <View style={styles.fields}>
        <InputComponent
          placeholder="Username"
          mode="text"
          value={username}
          onChangeText={setUsername}
        />
        <InputComponent
          placeholder="Email"
          mode="email"
          value={email}
          onChangeText={setEmail}
        />
        <InputComponent
          placeholder="Password"
          mode="text"
          secure={true}
          value={password}
          onChangeText={setPassword}
        />
        <InputComponent
          placeholder="Confirm Password"
          mode="text"
          secure={true}
          value={confirmPassword}
          onChangeText={setConfirmPassword}
        />
        <RoundedButton
          title="Register"
          btnColor={Colors.black}
          onPressedFun={registerFun}
        />
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  root: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: Colors.gray,
  },
  svgtext: {
    gap: 31,
    alignItems: "center",
    marginBottom: 30,
  },
  headerText: {
    fontSize: 20,
    fontWeight: "700",
  },
  fields: {
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    gap: 20,
  },
});

export default RegisterScreen;
