import React, { useState } from "react";
import { StyleSheet, Image, Text, View, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native"; // Import useNavigation from React Navigation
import HamburgerMenu from "../../assets/svg/HamburgerMenu";
import LogoSvg from "../../assets/svg/LogoSvg";

const Heading = () => {
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const navigation = useNavigation(); // Get the navigation object

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  const navigateToAnotherPage = (pageName) => {
    // Use navigation to navigate to another page
    navigation.navigate(pageName);
    toggleDropdown(); // Close the dropdown after navigating
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={toggleDropdown}>
        <View style={styles.headerRoot}>
          <Image
            source={require("../../assets/images/logo.png")}
            width={10}
            height={10}
            resizeMode="center"
          />
          <HamburgerMenu />
        </View>
      </TouchableOpacity>
      {isDropdownVisible && (
        <View style={styles.dropdown}>
          {/* Add your dropdown buttons here */}
          <TouchableOpacity onPress={() => navigateToAnotherPage("IncidentReportForm")}>
            <Text>Create Incident Report</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => navigateToAnotherPage("TipsPage")}>
            <Text>Add Tip and View</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => navigateToAnotherPage("AdminReport")}>
            <Text>View Report</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => navigateToAnotherPage("AdminTips")}>
            <Text>Tip update</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => navigateToAnotherPage("Login")}>
            <Text>Log Out</Text>
          </TouchableOpacity>
          {/* Add more buttons as needed */}
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: "relative",
  },
  headerRoot: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginHorizontal: 35,
  },
  dropdown: {
    position: "absolute",
    top: "100%",
    right: 0,
    backgroundColor: "white",
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
  },
});

export default Heading;
