import React, { useState, useEffect } from "react";
import { View, Text, TextInput, Button, FlatList, StyleSheet } from "react-native";
import Heading from "../components/landing-screen/Heading"; // Import the Heading component

const TipsPage = () => {
  const [tipText, setTipText] = useState("");
  const [tips, setTips] = useState([]);
  const [user, setUser] = useState(null);
  useEffect(() => {
    // Fetch user data from your Express backend
    fetch("http://localhost:3006/safety_tip/user", {
      method: "GET",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((userData) => {
        // Update the user state with the retrieved user data
        setUser(userData);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        // Handle the error appropriately, e.g., show an error message to the user
      });
  }, []);




