import React, { useState } from 'react';

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    date: '',
    location: '',
    incidentType: '',
    description: '',
  });

  // Add a state to store the filtered locations
  const [filteredLocations, setFilteredLocations] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setIncident({
      ...incident,
      [name]: value,
    });

    // Filter and update the suggestions based on the input value
    filterLocations(value);
  };

  const filterLocations = (searchTerm) => {
    // Replace this with your logic to fetch and filter locations
    // For example, you can fetch a list of locations from an API
    // and filter them based on the searchTerm
    const filteredLocations = yourFilterFunction(searchTerm);

    setFilteredLocations(filteredLocations);
  };

  const handleLocationClick = (location) => {
    // Set the selected location from the suggestions
    setIncident({
      ...incident,
      location: location,
    });

    // Clear the suggestions
    setFilteredLocations([]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to a server, save to a database)
    console.log('Incident data:', incident);
    // Clear the form fields after submission
    setIncident({
      date: '',
      location: '',
      incidentType: '',
      description: '',
    });
  };

  return (
    <div className="container">
      {/* Your CSS styles */}
      <h2>Incident Reporting Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={incident.date}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={incident.location}
            onChange={handleChange}
          />
          {/* Display location suggestions */}
          {filteredLocations.length > 0 && (
            <ul className="suggestions">
              {filteredLocations.map((location, index) => (
                <li key={index} onClick={() => handleLocationClick(location)}>
                  {location}
                </li>
              ))}
            </ul>
          )}
        </div>
        {/* Rest of your form */}
        <button type="submit" className="btn mt-3">
          Submit
        </button>
      </form>
    </div>
  );
};

export default IncidentReportForm;
