import React, { useState } from 'react';

const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    date: '',
    location: '',
    incidentType: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setIncident({
      ...incident,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to a server, save to a database)
    console.log('Incident data:', incident);
    // Clear the form fields after submission
    setIncident({
      date: '',
      location: '',
      incidentType: '',
      description: '',
    });
  };

  return (
    <div className="container">
      <style>
        {`
        .container {
          max-width: 400px;
          margin: 0 auto;
          padding: 20px;
          /* Remove the border */
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
          font-family: 'Poppins', sans-serif; /* Change the font to Poppins */
        }

        h1 {
          text-align: center;
        }

        h2 {
          text-align: center;
        }

        // .form-group {
        //   margin-bottom: 20px;
        // }

        label {
          display: block;
          font-weight: bold;
        }

        input, select, textarea {
          width: 100%;
          padding: 8px;
          border: 1px solid #ccc;
          border-radius: 4px;
          font-family: 'Poppins', sans-serif; /* Change the font to Poppins */
        }

        select {
          height: 36px;
        }

        textarea {
          height: 100px;
        }

        .btn {
          display: block;
          width: 100%;
          padding: 10px;
          background-color: #007bff;
          color: #fff;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          font-family: 'Poppins', sans-serif; /* Change the font to Poppins */
        }

        .btn:hover {
          background-color: #0056b3;
        }
        `}
      </style>
      <h1>Incident Reporting</h1> {/* Add the heading here */}
      <h2>Report an Incident</h2>
      <form onSubmit={handleSubmit}>
        {/* ... rest of the form ... */}
      </form>
    </div>
  );
};

export default IncidentReportForm;
