// ProfileScreen.js
import React, { useState, useEffect } from 'react';
import Heading from '../components/landing-screen/Heading';
import { View, Text, Image, TextInput, TouchableOpacity, StyleSheet, FlatList, Animated } from 'react-native';
import { setBackgroundColor } from '../components/global/BackgroundColor';
import { Colors } from "../assets/Colors";
import HomeScreen from './HomeScreen';
import Modal from 'react-native-modal';
import { SafeAreaView } from 'react-native-safe-area-context';

const ProfileScreen = () => {
  const [name, setName] = useState('John Doe');
  const [username, setUsername] = useState('johndoe');
  const [email, setEmail] = useState('johndoe@example.com');
  const [contact, setContact] = useState('061 234 5678');
  const [isEditing, setIsEditing] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);

  useEffect(() => {
    // Call setBackgroundColor with the desired color
    setBackgroundColor(Colors.darkGray);
  }, []);

  const animatedValue = new Animated.Value(0);

  const animateText = () => {
    Animated.sequence([
      Animated.timing(animatedValue, {
        toValue: 1,
        duration: 1000, // Increase the size in 1 second
        useNativeDriver: false,
      }),
      Animated.timing(animatedValue, {
        toValue: 0,
        duration: 1000, // Decrease the size in 1 second
        useNativeDriver: false,
      }),
    ]).start();
  };

  const saveChanges = () => {
    setIsEditing(false);
    setIsModalVisible(true);
    animateText(); // Start the animation when saving changes
    // You can implement code here to save the edited details to your backend or storage.
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  const data = [
    { label: 'Name', value: name },
    { label: 'Username', value: username },
    { label: 'Email', value: email },
    { label: 'Contact', value: contact },
  ];

  return (
    <SafeAreaView style={styles.root}>
       <View style={styles.container}>



      <View style={styles.container2}  >

      <Text style={styles.listTitle} textAlign="center" fontWeight="bold">
        Update your Profile
      </Text>
      <Image

        style={styles.profileImage}
      />
      <FlatList
        data={data}
        keyExtractor={(item) => item.label}
        renderItem={({ item }) => (
          <View style={styles.infoRow}>
            <Text style={styles.label}>{item.label}:</Text>
            {isEditing ? (
              <TextInput
                style={styles.editInput}
                value={item.value}
                onChangeText={(text) => {
                  if (item.label === 'Name') setName(text);
                  if (item.label === 'Username') setUsername(text);
                  if (item.label === 'Email') setEmail(text);
                  if (item.label === 'Contact') setContact(text);
                }}
                placeholder={`Enter ${item.label}`}
                placeholderTextColor="grey"
              />
            ) : (
              <Animated.Text style={[styles.value, { fontSize: animatedValue.interpolate({
                inputRange: [0, 1],
                outputRange: [16, 20], // Change the font size during the animation
              }) }]}>{item.value}</Animated.Text>
            )}
          </View>
        )}
      />
      {isEditing ? (
        <TouchableOpacity style={styles.editButton} onPress={saveChanges}>
          <Text style={styles.editButtonText}>Save Changes</Text>
        </TouchableOpacity>
      ) : (
        <TouchableOpacity
          style={styles.editButton}
          onPress={() => setIsEditing(true)}
        >
          <Text style={styles.editButtonText}>Edit Profile</Text>
        </TouchableOpacity>
      )}
      <Modal isVisible={isModalVisible}>
        <View style={styles.modalContent}>
          <Text style={styles.modalText}>Profile Updated</Text>
          <TouchableOpacity onPress={closeModal}>
            <Text style={styles.closeModalText}>Close</Text>
          </TouchableOpacity>
        </View>
      </Modal>


      </View>


    </View>

    <View style={styles.bar}>
      <HomeScreen/>
      </View>

      </SafeAreaView>

  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: 'flex-start',
    margin:15,
    backgroundColor:Colors.gray,
  },
  container2:{
    justifyContent: 'flex-start',
    margin:20,
    backgroundColor:Colors.gray,
  },


  root:{flex:1,
    backgroundColor:"#cae7d3",
  },
  profileImage: {
    width: 150,
    height: 150,
    borderRadius: 75,
    marginBottom: 20,
    backgroundColor:Colors.white,
  },
  infoRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1,
    borderColor: 'lightgrey',
    padding: 10,
    borderRadius: 5,

  },
    bar:{
      marginTop:130,
      borderRadius:5,
    },
  listTitle: {
    fontSize: 18,
    flexDirection:'',
    fontWeight: "bold",
    margin: 40,
    textAlign: "center", 
  },
  label: {
    width: 100,
    fontSize: 16,
    fontWeight: 'bold',
  },
  value: {
    flex: 1,
    fontSize: 16,
    color: 'gray',
  },
  editInput: {
    flex: 1,
    height: 40,
    backgroundColor: 'white',
    borderColor: 'grey',
    borderWidth: 1,
    borderRadius: 5,
    paddingLeft: 10,
    color: 'grey',
  },
  editButton: {
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 5,
    marginBottom:50,
  },
  editButtonText: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  modalText: {
    fontSize: 20,
  },
  closeModalText: {
    color: 'blue',
    marginTop: 10,
  },
});

export default ProfileScreen;
