import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  FlatList,
  StyleSheet,
  ActivityIndicator,
} from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

import { BASE_URL } from '../assets/constants/ConstantData';
import { SafeAreaView } from 'react-native-safe-area-context';
import HomeScreen from './HomeScreen';

const TipsPage = ({ citizen }) => {
  const [tipText, setTipText] = useState('');
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [citizenId, setCitizenId] = useState('');
  const [citizenName, setCitizenName] = useState('');

  const getItemStorage = async () => {

    const keys = ['citizen_id', 'citizen_name'];
    AsyncStorage.multiGet(keys)
      .then((keyValuePairs) => {
        keyValuePairs.forEach(([key, value]) => {
          if(key === 'citizen_id') setCitizenId(value)
          if (key === 'citizen_name') setCitizenName(value)
          console.log(`Key: ${key}, Value: ${value}`);
        });
      })
      .catch((error) => {
        console.error('Error retrieving data: ', error);
    });
  }
  useEffect(() => {
    // Fetch safety tips when the component mounts
    getItemStorage() 
    fetchSafetyTips();
  }, []);

  const welcomeMessage = `Welcome ${citizenName}`;

  const fetchSafetyTips = () => {
    setLoading(true);
    axios
      .get(`${BASE_URL}:3006/safety_tip/`)
      .then((response) => {
        // Sort the tips by date in descending order (newest first)
        const sortedTips = response.data.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        setTips(sortedTips);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching safety tips");
        setLoading(false);
        console.error("Error fetching safety tips:", error);
      });
  };

  const handleAddTip = () => {
    console.log("ID F POST " + citizenId)

    var tip = { tip_description: tipText, citizen_id: citizenId };

    if (!tipText) {
      setError('Tip text or citizen information is missing.');
      return;
    }

    console.log(tip)

    setLoading(true);
    axios
      .post(`${BASE_URL}:3006/safety_tip`, tip, { headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        console.log('Tip added successfully:', response.data);
        fetchSafetyTips();
        setTipText('');
        setError(null);
      })
      .catch((error) => {
        setError('Error adding safety tip');
        console.error('Error adding safety tip:', error);
      })
      .finally(() => {
        setLoading(false);
      });
  };
/*gvgyvgyyyununini*/
  const formatDate = (dateString) => {
    const dateObj = new Date(dateString);
    return dateObj.toLocaleDateString(); // Formats the date as per locale
  };

  return (
    <SafeAreaView style={styles.container}>

      <View >
      <Text style={styles.listTitle} textAlign="center">
        Alert Community
      </Text>
      <View style={styles.wel}>
        <Text style={styles.welcomeMessage}>{welcomeMessage}!</Text>

      </View>



      {loading ? (
        <ActivityIndicator size="large" color="black" />
      ) : (
        <FlatList
          data={tips}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({ item }) => (
            <View>
              <View style={styles.commentContainer}>
                <Text style={styles.tipAndUser}>
                  <Text style={[styles.username, { textAlign: 'center' }]}>
                    {item.citizenName}:
                  </Text>{' '}
                  <Text style={styles.tip} textAlign="center">
                    {item.tip_description}

                  </Text>
                </Text>
              </View>
              <Text style={styles.date}>
                Date: {formatDate(item.date)}
              </Text>
            </View>

          )}
          ListEmptyComponent={() => (
            <Text style={[styles.emptyList, { textAlign: 'center' }]}>
              No tips yet
            </Text>
          )}
        />
      )}
      {error && (
        <Text style={[styles.errorText, { textAlign: 'center' }]}>{error}</Text>
      )}
      <View style={styles.inputContainer}>
        <TextInput
          style={[styles.input,{color:'black'}]}
          placeholder="Enter your tip here"
          value={tipText}
          onChangeText={(text) => setTipText(text)}
          multiline
        />
        <View style={styles.buttonContainer}>
          <Pressable onPress={handleAddTip} style={styles.pressableButton}>
            <Text style={[styles.buttonText, { color: 'white', textAlign: 'center' }]}>Add Tip</Text>
          </Pressable>
        </View>
      </View>
      <HomeScreen />
    </View>

    </SafeAreaView>

  );
};

const styles = StyleSheet.create({
  wel: {
    flex: 1,
    flexDirection: 'row', 
    alignItems: 'center', 
  },
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: "#E3E9EC",
    elevation: 12, 
  },
  listTitle: {
    fontSize: 18,
    flexDirection:'',
    fontWeight: "bold",
    marginBottom: 8,
    textAlign: "center", 
  },
  commentContainer: {
    borderWidth: 1,
    borderColor: "#ccc",
    width: 260,
    height:70,
    borderRadius: 4,
    backgroundColor: "black",
    padding: 8,
    marginBottom: 8, // Add margin at the bottom
  },
  tipAndUser: {
    flexDirection: "row",
    alignItems: "center",
  },
  username: {
    fontWeight: 'bold',
    marginRight: 8,
    textAlign: 'center',
  },
  tip: {
    fontSize: 16,
    textAlign: "center",
  },
  emptyList: {
    fontSize: 16,
    color: "#777",
  },
  inputContainer: {
    flexDirection: "column",
    color:'white',
    alignItems: "stretch",
    marginTop: 16,

  },
  input: {
    width: 363,
    height: 51,
    marginBottom: 8,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 6,
    padding: 8,
    textAlign: "center",
  },
  buttonContainer: {
    width: 82,
    height: 28,
    backgroundColor: "black",
    alignSelf: "flex-end",
    borderRadius: 6,
  },
  date: {
    fontSize: 14,
    textAlign: 'center',
    color: '#777',
    marginLeft: 16,
  },  
  welcomeMessage: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'left',
    marginLeft: 16,
    marginTop: 16,
    marginBottom: 16,
  },
});

export default TipsPage;
