import HomeScreen from './HomeScreen';
import RoundedButton from '../components/global/RoundedButton';
import Heading from '../components/landing-screen/Heading';
import { Colors } from '../assets/Colors';
import { SafeAreaView } from 'react-native-safe-area-context';
//import { black } from 'react-native-paper/lib/typescript/styles/themes/v2/colors';
const incidentTypes = [
  { label: 'Car jacking', value: 'Car jacking' },
  { label: 'Robbery', value: 'Robbery' },
  { label: 'Kidnapping', value: 'Kidnapping' },
  { label: 'Other', value: 'Other' },
];
const IncidentReportForm = () => {
  const [incident, setIncident] = useState({
    location: '',
    incidentType: '',
    description: '',
    customIncidentType: '',
  });
  const handleChange = (name, value) => {
    if (name === 'incidentType') {
      setIncident({
        ...incident,
        incidentType: value,
        customIncidentType: '', // Clear custom incident type when a predefined type is selected
      });
    } else if (name === 'customIncidentType') {
      setIncident({
        ...incident,
        customIncidentType: value,
      });
    } else {
      setIncident({
        ...incident,
        [name]: value,
      });
    }
  };
  const handleSubmit = () => {
    const { location, incidentType, description, customIncidentType } = incident;
    // Your axios request here
    // Replace the axios request with the appropriate code for making a network request in React Native
  };
  const handleIncidentTypeChange = (value) => {
    if (value === 'Other') {
      handleChange('description', ''); // Clear description when 'Other' is selected
    }
    handleChange('incidentType', value);
  };
  return (
    <SafeAreaView style={styles.root}>



    <View style={styles.bar}>
    <HomeScreen

     />
  </View>


    </SafeAreaView>

  );
};
const styles = {
  root:{flex:1,
    backgroundColor:'#cae7d3',
    justifyContent:'center',
    alignItems:'center',
  },
  editButton: {
    backgroundColor: 'black',
    padding: 10,
    width:15,
    borderRadius: 5,
    marginBottom:50,
  },
  container: {
    justifyContent: 'center',
    margin:15,
    borderRadius:15,
    backgroundColor:Colors.gray,
    borderRadius: 20,
  },
  container2:{
    justifyContent: 'flex-start',
    margin:20,
    backgroundColor:Colors.gray,
    borderRadius:15,

  },

  btn:{
    justifyContent:'center',
    alignItems:'center'

  },



  title: {
    fontSize: 24,
    textAlign: 'center',
    marginBottom: 20,
    fontWeight: 'bold',
  },
  input: {
    borderWidth: 1,
    borderColor: 'lightgray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
    backgroundColor: '#fff',
  },
  label: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  radioContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
    Color:'black',
  },
  root:{flex:1,
    backgroundColor:'#cae7d3',

  },
  textArea: {
    borderWidth: 1,
    borderColor: 'lightgray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
    minHeight: 100,
  },
};
export default IncidentReportForm;
